mapper.select:
select

from `column`
where 1=1

    and user_id = #{userId,jdbcType=BIGINT}


    and table_id = #{tableId,jdbcType=BIGINT}

limit #{startIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select
count(1)
from `column`
where 1=1

    and user_id = #{userId,jdbcType=BIGINT}


    and table_id = #{tableId,jdbcType=BIGINT}

select

from `column`
where 1=1

    and `name` = #{name,jdbcType=VARCHAR}


    and table_id = #{tableId,jdbcType=BIGINT}


    and user_id = #{userId,jdbcType=BIGINT}
