mapper.select:
select

from tables
where 1=1

    and user_id = #{userId,jdbcType=BIGINT}


    and db_id = #{dbId,jdbcType=BIGINT}

limit #{startIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select
count(1)
from tables
where 1=1

    and user_id = #{userId,jdbcType=BIGINT}


    and db_id = #{dbId,jdbcType=BIGINT}

select

from tables
where 1=1

    and `name` = #{name,jdbcType=VARCHAR}


    and db_id = #{dbId,jdbcType=BIGINT}


    and user_id = #{userId,jdbcType=BIGINT}
